mapper.select:
SELECT postnum, posttitle, posteddate
FROM posts${code} WHERE category = #{category} 
ORDER BY postnum DESC;

SELECT COUNT(*) 
FROM posts${code} 
WHERE postnum = #{postnum};

mapper.insert:
INSERT INTO posts${code} 
(category, posttitle, postcontent, powriteremail) 
VALUES(#{category}, #{posttitle}, 
#{postcontent}, #{powriteremail});

mapper.update:
UPDATE posts${code} 
SET category = #{category}, posttitle = #{posttitle}, 
postcontent = #{postcontent} where postnum = #{postnum};

mapper.select:
SELECT COUNT(*) 
FROM comments${code}
WHERE postnum = #{postnum};

SELECT co.commentnum, co.commentcontent, 
co.cowriteremail, u.nickname 
FROM comments${code} co 
INNER JOIN users u ON co.cowriteremail = u.email 
WHERE co.postnum = #{postnum} 
ORDER BY co.commentnum ASC;

mapper.insert:
INSERT INTO comments${code} 
(postnum, commentcontent, cowriteremail) 
VALUES(#{postnum}, #{commentcontent}, #{cowriteremail});

mapper.update:
UPDATE comments${code} 
SET commentcontent = #{commentcontent} 
WHERE commentnum = #{commentnum};

mapper.select:
SELECT COUNT(*) 
FROM reples${code}
WHERE commentnum = #{commentnum};

SELECT re.replenum, re.replecontent, 
re.rewriteremail, u.nickname 
FROM reples${code} re INNER JOIN users u 
ON re.rewriteremail = u.email
WHERE re.commentnum = #{commentnum} 
ORDER BY re.replenum ASC;

mapper.insert:
INSERT INTO reples${code} 
(postnum, commentnum, replecontent, rewriteremail)
VALUES (#{postnum}, #{commentnum}, 
#{replecontent}, #{rewriteremail});

mapper.update:
UPDATE reples${code} 
SET replecontent = #{replecontent} 
WHERE replenum = #{replenum};
